import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  padding: 20px;
  display: flex;
  justify-content: space-between;
`

const Rectangle = styled.div`
  width: ${props => props.isActive ? '300px' : '200px'};
  height: ${props => props.isActive ? '200px' : '100px'};
  background-color: ${props => props.isActive ? 'red' : props.isSelected? 'black' : 'transparent'};
  border-width: thin;
`;
const RoundedRectangle = styled.div`
  width: ${props => props.isActive ? '300px' : '200px'};
  height: ${props => props.isActive ? '200px' : '100px'};
  background-color: ${props => props.isActive ? 'red' : props.isSelected? 'black' : 'transparent'};
  border-width: thin;
  border-radius: 5px;
`;
const Circle = styled.div`
  width: ${props => props.isActive ? '300px' : '200px'};
  height: ${props => props.isActive ? '200px' : '100px'};
  background-color: ${props => props.isActive ? 'red' : props.isSelected? 'black' : 'transparent'};
  border-width: thin;
  border-radius: 100%;
`

export default () => {

  const shapes = [
    {
      id: 'rectangle',
      Shape: Rectangle,
      isSelected: true
    },
    {
      id: 'rounded-rectangle',
      Shape: RoundedRectangle,
      isSelected: false
    },
    {
      id: 'circle',
      Shape: Circle,
      isSelected: false
    }
  ]

  const [ActiveShape, setActiveShape] = useState(Rectangle)

  return(
    <Container>
      <ActiveShape isActive />
      {
        shapes.map(item => {
          const { Shape, isSelected } = item;
          return(
            <Shape isSelected={isSelected} />
          )
        })
      }
    </Container>
  )
}
